import React, { useState, useEffect } from 'react'

function IntervalCounter() {
    const [count, setCount] = useState(0);

    const tick = () => {
        setCount(count + 1)
    }

    useEffect(() => {
       const interval = setInterval(tick, 1000)

       return () => {
           clearInterval(interval)
       }
    }, [input])

    return (
        <div>
            {count}
        </div>
    )
}

export default IntervalCounter
